import { useRef, useState } from 'react'
import { getWeather } from './weather';
import { makeWidget } from './model';
import '../styles/styles.css';

const Main = () => {
    const input = document.querySelector('#search');
    const submitSearch = document.querySelector('#search + button');
    const checked = document.getElementById('checkbox');
    const temptoggle = document.querySelector('.temptoggle');
    let currentObjRef;

    const citySearchRef = useRef();
    const widgetRef = useRef();
    const [weather, setWeather] = useState('');

    function printElement(e) {
        e.preventDefault();
        const input = e.target.firstChild.value;
        const childNodes = widgetRef.current.childNodes;
        let output = {};
        const re = new RegExp(`${e.target.firstChild.value}`, 'g');
        childNodes.forEach((node) => {
            if (node.classList.value.search(re) !== -1) {
                output = { ...output, [node.classList.value]: node.lastChild.textContent };
            }
        });
        const dataStr = "data:text/json;charset=utf-8," + encodeURIComponent(JSON.stringify(output));
        const dlAnchorElem = document.getElementById('downloadAnchorElem');
        dlAnchorElem.setAttribute("href", dataStr);
        dlAnchorElem.setAttribute("download", "data.json");
        dlAnchorElem.click();
    }

    function filterElem(e) {
        const childNodes = widgetRef.current.childNodes;
        if (e.target.value === '') { //no search query
            childNodes.forEach((node) => {
                node.style.display = 'block';
            });
        } else {
            const re = new RegExp(`${e.target.value}`, 'g');
            childNodes.forEach((node) => {
                if (node.classList.value.search(re) !== -1) {
                    node.style.display = 'block';
                } else {
                    node.style.display = 'none';
                }
            })
        }
    }


    function callAPI(e) {
        e.preventDefault();
        const city = citySearchRef.current.value;
        let promise = getWeather(city);
        promise.then((result, error) => {
            if (error) {
                throw new Error();
            }
            // appendValues(result);
            // toggleForm();
            // toggleWidget();
            // currentObjRef = result;
            console.log(result);
            const widget = makeWidget(result, widgetRef);
            setWeather(widget);
        });
    }


    function tempToggleHand() {
        // appendValues(currentObjRef);
    }

    function xBtnEventHand(event) {
        // event.target.checked = true;
        // resetTempToggle();
        // toggleForm();
        // toggleWidget();
    }

    function submitHand(event) {
        event.preventDefault(); //prevent page from refreshing
        callAPI(input.value);
        input.value = '';
    }

    //temptoggle.addEventListener('click', tempToggleHand);
    //submitSearch.addEventListener('click', submitHand);
    //checked.addEventListener('change', xBtnEventHand);


    return (
        <section>
            <nav>
                Nav goes here
            </nav>
            <form onSubmit={(e) => callAPI(e)}>
                <label for='citysearch'></label>
                <input ref={citySearchRef} type='text' id='citysearch' name='citysearch' placeholder="Enter a City to Search" />
                <button>Submit</button>
            </form>
            {weather}
            <form onChange={(e) => filterElem(e)} onSubmit={(e) => printElement(e)}>
                <input type='text' placeholder={`Type to filter, submit to save`} />
                <button>Submit</button>
                <a href='' id="downloadAnchorElem" style={{ 'display': 'none' }}>{''}</a>
            </form>
        </section>
    )
}

export default Main;
